const express = require("express");
const { createProject, getProjectById, updateProject, deleteProject } = require("../controllers/projectController");

const router = express.Router();

router.post("/projects", createProject); // Create Project
router.get("/projects/:id", getProjectById); // Get Single Project
router.put("/projects/:id", updateProject); // Update Project
router.delete("/projects/:id", deleteProject); // Delete Project

module.exports = router;
